import React, { useEffect, useState, useRef } from "react";
import styles from "../styles/mappage.module.scss";

declare global {
    interface Window {
        kakao: any;
    }
}

interface MapPageProps {
    selectedPlace: { lat: number, lng: number } | null;
    login: boolean;
    setLogin: React.Dispatch<React.SetStateAction<boolean>>;
}

function MapPage({ selectedPlace, login, setLogin }: MapPageProps) {
    const mapRef = useRef<Window.kakao.maps.Map | null>(null); // 맵 객체를 위한 타입 지정
    const markerRef = useRef<window.kakao.maps.Marker | null>(null); // 마커 객체를 위한 타입 지정

    useEffect(() => {
        const container = document.getElementById('map');
        if (container) {
            const options = {
                center: new window.kakao.maps.LatLng(33.450701, 126.570667),
                level: 3
            };
            const map = new window.kakao.maps.Map(container, options);
            mapRef.current = map;

            const marker = new window.kakao.maps.Marker({
                position: new window.kakao.maps.LatLng(33.450701, 126.570667),
                map: map,
                title: "현재 위치"
            });
            markerRef.current = marker;
        }
    }, []);

    useEffect(() => {
        if (selectedPlace && mapRef.current && markerRef.current) {
            const newPosition = new window.kakao.maps.LatLng(selectedPlace.lat, selectedPlace.lng);
            markerRef.current.setPosition(newPosition);
            mapRef.current.setCenter(newPosition);
        }
    }, [selectedPlace]);

    return (
        <div id="map" className={styles.map}></div>
    );
}

export default MapPage;
