import React, { useEffect, useState } from 'react';
import styles from '../styles/createpostpage.module.scss';
import flat from "../images/flat.png";

declare global {
    interface Window {
        kakao: any;
    }
}

interface CreatePostPageProps {
    setSelectedPlace: (place: { lat: number, lng: number }) => void;
}

function CreatePostPage({ setSelectedPlace }: CreatePostPageProps) {
    const [address, setAddress] = useState<string>('');
    const [places, setPlaces] = useState<any[]>([]);
    const [selectedPlace, setSelectedPlaceState] = useState<any>(null);

    useEffect(() => {
        if (!address || !window.kakao || !window.kakao.maps) return;

        const ps = new window.kakao.maps.services.Places();
        ps.keywordSearch(address, (data: any[], status: any) => {
            if (status === window.kakao.maps.services.Status.OK) {
                setPlaces(data);
            }
        });
    }, [address]);

    useEffect(() => {
        if (!selectedPlace || !window.kakao || !window.kakao.maps) return;

        const container = document.getElementById('map');
        const options = {
            center: new window.kakao.maps.LatLng(selectedPlace.y, selectedPlace.x),
            level: 3,
        };
        const map = new window.kakao.maps.Map(container, options);
        const markerPosition = new window.kakao.maps.LatLng(selectedPlace.y, selectedPlace.x);
        const marker = new window.kakao.maps.Marker({
            position: markerPosition,
        });
        marker.setMap(map);
    }, [selectedPlace]);

    const handleSelect = (place: any) => {
        setSelectedPlaceState(place);
        setSelectedPlace({ lat: place.y, lng: place.x });
        setAddress(place.address_name);
        setPlaces([]);
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (!selectedPlace) {
            alert('장소를 선택해주세요.');
            return;
        }

        const container = document.getElementById('map');
        const options = {
            center: new window.kakao.maps.LatLng(selectedPlace.y, selectedPlace.x),
            level: 3,
        };
        const map = new window.kakao.maps.Map(container, options);
        const markerPosition = new window.kakao.maps.LatLng(selectedPlace.y, selectedPlace.x);
        const marker = new window.kakao.maps.Marker({
            position: markerPosition,
        });
        marker.setMap(map);
    };

    const onClick = (e: React.FormEvent) => {
        e.preventDefault();
        if (!selectedPlace) {
            alert('장소를 선택해주세요.');
            return;
        }

        const container = document.getElementById('map');
        const options = {
            center: new window.kakao.maps.LatLng(selectedPlace.y, selectedPlace.x),
            level: 3,
        };
        const map = new window.kakao.maps.Map(container, options);
        const markerPosition = new window.kakao.maps.LatLng(selectedPlace.y, selectedPlace.x);

        const imageSrc = flat, // 마커이미지의 주소입니다
            imageSize = new window.kakao.maps.Size(64, 69), // 마커이미지의 크기입니다
            imageOption = { offset: new window.kakao.maps.Point(27, 69) }; // 마커이미지의 옵션입니다. 마커의 좌표와 일치시킬 이미지 안에서의 좌표를 설정합니다.

        // 마커의 이미지정보를 가지고 있는 마커이미지를 생성합니다
        const markerImage = new window.kakao.maps.MarkerImage(imageSrc, imageSize, imageOption);

        // 마커를 생성합니다
        const marker = new window.kakao.maps.Marker({
            position: markerPosition,
            image: markerImage // 마커이미지 설정
        });

        // 마커가 지도 위에 표시되도록 설정합니다
        marker.setMap(map);
    };

    return (
        <div className={styles.pageContainer}>
            <div className={styles.formContainer}>
                <form onSubmit={handleSubmit}>
                    <div className={styles.formGroup}>
                        <label htmlFor="title">제목</label>
                        <input type="text" id="title" placeholder="제목" />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="date">날짜</label>
                        <input type="date" id="date" placeholder="날짜" />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="time">시간</label>
                        <input type="time" id="time" placeholder="시간" />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="location">장소</label>
                        <input
                            type="text"
                            id="location"
                            placeholder="주소로 검색하세요."
                            value={address}
                            onChange={(e: React.ChangeEvent<HTMLInputElement>) => setAddress(e.target.value)}
                        />
                        {places.length > 0 && (
                            <div className={styles.autocompleteDropdownContainer}>
                                {places.map((place) => (
                                    <div
                                        key={place.id}
                                        className={styles.suggestionItem}
                                        onClick={() => handleSelect(place)}>
                                        {place.place_name} ({place.address_name})
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                    <div id="map" className={styles.map}></div>
                    <div className={styles.formGroup}>
                        <label htmlFor="people">인원 수</label>
                        <select id="people" className={styles.select}>
                            <option value="" disabled selected>인원 수</option>
                            <option value="1">1명</option>
                            <option value="2">2명</option>
                            <option value="3">3명</option>
                            <option value="4">4명</option>
                            <option value="5">5명</option>
                            <option value="6">6명</option>
                            <option value="7">7명</option>
                            <option value="8">8명</option>
                            <option value="9">9명</option>
                            <option value="10">10명 이상</option>
                        </select>
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="description">자세한 설명</label>
                        <textarea id="description" placeholder="자세한 설명을 입력해주세요." />
                    </div>
                    <div className={styles.buttonGroup}>
                        <button type="submit" className={styles.submitButton} onClick={onClick}>작성완료 ➔</button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default CreatePostPage;
