import React from 'react';
import NoticeItem from '../components/noticepagecomponent/NoticeItem';
import styles from '../styles/noticepage.module.scss';
import notice1 from '../images/notice1.png';
import notice2 from '../images/notice2.png';
import notice3 from '../images/notice3.png';
import notice4 from '../images/notice4.png';
import {noticedata} from "../util/noticedata";

function NoticePage() {
    return (
        <div className={styles.pageContainer}>
            <div className={styles.noticeContainer}>
                <div className={styles.noticeList}>
                    {noticedata.map((post, index) => (
                        <NoticeItem date={post.date} title={post.title} description={post.description} views={post.views} imgSrc={post.imgSrc}/>
                        ))}
                    <NoticeItem
                        date="2024년 07월 03일"
                        title="플로깅과 함께하는 ECO 마라톤"
                        description="조선해쉬테라프 호텔에서 주관하는 에코 마라톤이 열립니다. ECO 마라톤에 참가할 수 있는 이벤트가 열립니다."
                        views="450 views"
                        imgSrc={notice1} // 실제 이미지 경로로 수정
                    />
                    <NoticeItem
                        date="2024년 07월 02일"
                        title="세계 환경의 날 기념 플로깅 이벤트"
                        description="세계 환경의 날 기념으로 플로깅 이벤트가 열립니다. 함께 환경을 보호하는 시간을 가져봅시다."
                        views="1000 views"
                        imgSrc={notice2} // 실제 이미지 경로로 수정
                    />
                    <NoticeItem
                        date="2024년 07월 01일"
                        title="ECORUNNER, ABC마트와 함께하는 플로깅 챌린지!"
                        description="ECORUNNER와 ABC마트가 함께하는 플로깅 챌린지가 열립니다. 참가자 전원에게 기념품이 제공됩니다."
                        views="5000 Reviews"
                        imgSrc={notice3} // 실제 이미지 경로로 수정
                    />
                    <NoticeItem
                        date="2024년 07월 01일"
                        title="남이섬에서 진행하는 플로깅 이벤트!"
                        description="남이섬에서 플로깅 이벤트가 열립니다. 참가자 전원에게 기념품이 제공됩니다."
                        views="450 Reviews"
                        imgSrc={notice4} // 실제 이미지 경로로 수정
                    />
                </div>
            </div>
        </div>
    );
}

export default NoticePage;
