import React, { useEffect, useState } from "react";
import styles from "../styles/mappage.module.scss";
import axios from "axios";
import { Post } from "../interface/posts";

declare global {
    interface Window {
        kakao: any;
    }
}

interface MapPageProps {
    selectedPlace: { lat: number; lng: number } | null; // 예시 타입, 필요에 따라 수정
    login: boolean;
    setLogin: React.Dispatch<React.SetStateAction<boolean>>;
}

function MapPage({ selectedPlace, login, setLogin }: MapPageProps) {
    if (login) {
        setLogin(true);
    } else {
        setLogin(false);
    }
    const [center, setCenter] = useState<{ lat: number; lng: number }>({
        lat: 33.450701,
        lng: 126.570667,
    });
    const [position, setPosition] = useState<{ lat: number; lng: number }>({
        lat: 33.450701,
        lng: 126.570667,
    });
    const [posts, setPosts] = useState<Post[]>([]);

    useEffect(() => {
        // 사용자의 현재 위치를 가져오는 함수
        const getCurrentLocation = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(
                    (position) => {
                        const latitude = position.coords.latitude;
                        const longitude = position.coords.longitude;
                        setCenter({ lat: latitude, lng: longitude });
                        setPosition({ lat: latitude, lng: longitude });

                        // 지도 초기화
                        initializeMap(latitude, longitude);
                    },
                    (error) => {
                        console.error("Error retrieving location:", error);
                    }
                );
            } else {
                console.error("Geolocation is not supported by this browser.");
            }
        };

        const initializeMap = (latitude: number, longitude: number) => {
            const mapContainer = document.getElementById("map"); // 지도를 표시할 div
            const mapOption = {
                center: new window.kakao.maps.LatLng(latitude, longitude), // 사용자의 현재 위치로 중심좌표 설정
                level: 3, // 지도의 확대 레벨
            };

            // 지도를 표시할 div와 지도 옵션으로 지도를 생성합니다
            const map = new window.kakao.maps.Map(mapContainer, mapOption);

            // 현재 위치에 마커를 표시합니다
            const markerPosition = new window.kakao.maps.LatLng(latitude, longitude);
            const marker = new window.kakao.maps.Marker({
                position: markerPosition,
                title: "현재 위치",
            });
            marker.setMap(map);

            // 가져온 데이터로 마커 추가
            posts.forEach((post) => {
                const postMarkerPosition = new window.kakao.maps.LatLng(post.lat, post.lng);
                const postMarker = new window.kakao.maps.Marker({
                    position: postMarkerPosition,
                    title: post.title, // 포스트 제목을 마커 타이틀로 설정
                });
                postMarker.setMap(map);
            });
        };

        // 게시물 데이터를 가져오는 함수
        const fetchPosts = async () => {
            try {
                const response = await axios.get(`http://3.37.252.66:8080/index/articles`);
                if (response.status === 200) {
                    setPosts(response.data);
                } else {
                    alert("Post get failed. Please try again.");
                }
            } catch (error) {
                if (axios.isAxiosError(error) && error.response) {
                    alert(`Error: ${error.response.data}`);
                } else {
                    alert("Error during request");
                }
            }
        };

        fetchPosts().then(() => {
            // 게시물 데이터를 가져온 후 현재 위치 설정
            getCurrentLocation();
        });
    }, []); // 빈 배열을 전달하여 컴포넌트가 처음 렌더링될 때만 실행

    return <div id="map" className={styles.map}></div>;
}

export default MapPage;
