import React, { useState, useEffect } from 'react';
import styles from '../styles/createpostpage.module.scss';
import { Post } from "../interface/posts";
import { useNavigate } from "react-router-dom";
import axios from "axios";

declare global {
    interface Window {
        kakao: any;
    }
}

interface CreatePostPageProps {
    setSelectedPlace: (place: { lat: number, lng: number } | null) => void;
    addPost: (post: Post) => void;
    login: boolean;
    setLogin: React.Dispatch<React.SetStateAction<boolean>>;
}

function CreatePostPage({ setSelectedPlace, addPost, login, setLogin }: CreatePostPageProps) {
    const navigate = useNavigate();
    const baseUrl = 'http://3.37.252.66:8080';
    const [inputs, setInputs] = useState<Post>({
        id: 0,
        title: '',
        location: '',
        participants: '',
        date: '',
        time: '',
        views: 0,
        description: '',
        content: '',
        imagedata: '', // Assuming this will be the file path after upload
        lat: 0,
        lng: 0
    });

    const [selectedFile, setSelectedFile] = useState<File | null>(null);

    const onChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
        const { value, name } = e.target;

        if (name === 'id' || name === 'views') {
            setInputs(inputs => ({ ...inputs, [name]: parseInt(value) }));
        } else if (name === 'lat' || name === 'lng') {
            setInputs(inputs => ({ ...inputs, [name]: parseFloat(value) }));
        } else {
            setInputs(inputs => ({ ...inputs, [name]: value }));
        }
    };

    const onFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files && e.target.files[0]) {
            setSelectedFile(e.target.files[0]);
        }
    };

    const [places, setPlaces] = useState<any[]>([]);
    const [selectedPlaceState, setSelectedPlaceState] = useState<any>(null);

    useEffect(() => {
        if (!inputs.location || !window.kakao || !window.kakao.maps) return;

        const ps = new window.kakao.maps.services.Places();
        ps.keywordSearch(inputs.location, (data: any[], status: any) => {
            if (status === window.kakao.maps.services.Status.OK) {
                setPlaces(data);
            }
        });
    }, [inputs.location]);

    useEffect(() => {
        if (!selectedPlaceState || !window.kakao || !window.kakao.maps) return;

        const container = document.getElementById('map');
        const options = {
            center: new window.kakao.maps.LatLng(selectedPlaceState.y, selectedPlaceState.x),
            level: 3,
        };
        const map = new window.kakao.maps.Map(container, options);
        new window.kakao.maps.Marker({
            position: new window.kakao.maps.LatLng(selectedPlaceState.y, selectedPlaceState.x),
            map: map
        });
    }, [selectedPlaceState]);

    const handleSelect = (place: any) => {
        setSelectedPlaceState(place);
        setSelectedPlace({ lat: place.y, lng: place.x });
        setInputs(inputs => ({
            ...inputs,
            location: place.address_name,
            lat: parseFloat(place.y),
            lng: parseFloat(place.x)
        }));
        setPlaces([]);
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!selectedPlaceState || !selectedFile) {
            alert('Please select a place and an image.');
            return;
        }

        const formData = new FormData();
        formData.append('file', selectedFile);
        Object.keys(inputs).forEach(key => {
            if (key !== 'imagedata') { // Assuming imagedata should not be sent as part of formData
                formData.append(key, inputs[key as keyof Post]);
            }
        });

        try {
            const response = await axios.post(`${baseUrl}/index/articles/create`, formData, {
                headers: { 'Content-Type': 'multipart/form-data' }
            });
            if (response.status === 200) {
                alert('Post successful!');
                navigate('/community');
                addPost(inputs); // Assuming you might want to update state in parent component
            } else {
                alert('Post failed. Please try again.');
            }
        } catch (error) {
            if (axios.isAxiosError(error) && error.response) {
                alert(`Error: ${error.response.data}`);
            } else {
                alert('An unexpected error occurred.');
            }
        }
    };

    return (
        <div className={styles.pageContainer}>
            <div className={styles.formContainer}>
                <form onSubmit={handleSubmit}>
                    <div className={styles.formGroup}>
                        <label htmlFor="title">제목</label>
                        <input type="text" id="title" name="title" placeholder="제목" value={inputs.title} onChange={onChange} />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="image">이미지 파일</label>
                        <input type="file" id="image" name="image" onChange={onFileChange} />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="date">날짜</label>
                        <input type="date" id="date" name="date" value={inputs.date} onChange={onChange} />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="time">시간</label>
                        <input type="time" id="time" name="time" value={inputs.time} onChange={onChange} />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="location">장소</label>
                        <input type="text" id="location" name="location" placeholder="주소로 검색하세요." value={inputs.location} onChange={onChange} />
                        {places.length > 0 && (
                            <div className={styles.autocompleteDropdownContainer}>
                                {places.map(place => (
                                    <div key={place.id} className={styles.suggestionItem} onClick={() => handleSelect(place)}>
                                        {place.place_name} ({place.address_name})
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                    <div id="map" className={styles.map}></div>
                    <div className={styles.formGroup}>
                        <label htmlFor="participants">인원 수</label>
                        <select id="participants" name="participants" className={styles.select} value={inputs.participants} onChange={onChange}>
                            <option value="" disabled>인원 수</option>
                            <option value="1">1명</option>
                            <option value="2">2명</option>
                            <option value="3">3명</option>
                            <option value="4">4명</option>
                            <option value="5">5명</option>
                            <option value="6">6명</option>
                            <option value="7">7명</option>
                            <option value="8">8명</option>
                            <option value="9">9명</option>
                            <option value="10">10명 이상</option>
                        </select>
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="description">자세한 설명</label>
                        <textarea id="description" name="description" placeholder="자세한 설명을 입력해주세요." value={inputs.description} onChange={onChange} />
                    </div>
                    <div className={styles.buttonGroup}>
                        <button type="submit" className={styles.submitButton}>작성완료 ➔</button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default CreatePostPage;
