import React, { useState, useEffect } from 'react';
import styles from '../styles/createpostpage.module.scss';
import { Post } from "../interface/posts";
import axios from "axios";
import { useNavigate } from "react-router-dom";

// 전역 선언으로 카카오 맵 API 사용 선언
declare global {
    interface Window {
        kakao: any;
    }
}

// Props 정의
interface CreatePostPageProps {
    setSelectedPlace: (place: { lat: number, lng: number } | null) => void;
    addPost: (post: Post) => void;
}

// 게시글 생성 페이지 컴포넌트
function CreatePostPage({ setSelectedPlace, addPost }: CreatePostPageProps) {
    const navigate = useNavigate();
    const baseUrl = "http://3.37.252.66:8080";  // API 엔드포인트 주소
    const [inputs, setInputs] = useState<Post>({
        id: 0,
        title: '',
        location: '',
        participants: '',
        date: '',
        time: '',
        views: 0,
        description: '',
        content: '',
        imgSrc: '',
        lat: 0,
        lng: 0
    });
    const [image, setImage] = useState<File | null>(null);
    const [previewUrl, setPreviewUrl] = useState<string | null>(null);

    // 이미지 미리보기를 위한 useEffect
    useEffect(() => {
        if (!image) {
            setPreviewUrl(null);
            return;
        }
        const objectUrl = URL.createObjectURL(image);
        setPreviewUrl(objectUrl);

        return () => URL.revokeObjectURL(objectUrl);
    }, [image]);

    // 이미지 선택 핸들러
    const handleImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        if (event.target.files && event.target.files[0]) {
            setImage(event.target.files[0]);
        }
    };

    // 입력 핸들러
    const onChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
        const { value, name } = e.target;
        if (name === 'lat' || name === 'lng') {
            setInputs({...inputs, [name]: parseFloat(value)});
        } else {
            setInputs({...inputs, [name]: value});
        }
    };

    // 폼 제출 핸들러
    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!inputs.location) {
            alert('장소를 선택해주세요.');
            return;
        }

        const formData = new FormData();
        formData.append('title', inputs.title);
        formData.append('location', inputs.location);
        formData.append('participants', inputs.participants);
        formData.append('date', inputs.date);
        formData.append('time', inputs.time);
        formData.append('description', inputs.description);
        formData.append('content', inputs.content);
        if (image) formData.append('image', image);

        try {
            const response = await axios.post(`${baseUrl}/index/articles/create`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            if (response.status === 200) {
                alert('게시글이 성공적으로 등록되었습니다.');
                navigate('/community');
            } else {
                alert('게시글 등록에 실패하였습니다. 다시 시도해주세요.');
            }
        } catch (error) {
            console.error('POST 요청 중 오류 발생:', error);
            alert('게시글 등록 중 오류가 발생했습니다.');
        }

        setInputs({
            id: 0,
            title: '',
            location: '',
            participants: '',
            date: '',
            time: '',
            views: 0,
            description: '',
            content: '',
            imgSrc: '',
            lat: 0,
            lng: 0
        });
        setSelectedPlace(null);
    };

    // 컴포넌트 렌더링
    return (
        <div className={styles.pageContainer}>
            <div className={styles.formContainer}>
                <form onSubmit={handleSubmit}>
                    <div className={styles.formGroup}>
                        <label htmlFor="title">제목</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            placeholder="제목 입력"
                            value={inputs.title}
                            onChange={onChange}
                        />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="date">날짜</label>
                        <input
                            type="date"
                            id="date"
                            name="date"
                            placeholder="날짜 선택"
                            value={inputs.date}
                            onChange={onChange}
                        />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="time">시간</label>
                        <input
                            type="time"
                            id="time"
                            name="time"
                            placeholder="시간 입력"
                            value={inputs.time}
                            onChange={onChange}
                        />
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="location">장소</label>
                        <input
                            type="text"
                            id="location"
                            name="location"
                            placeholder="주소 검색"
                            value={inputs.location}
                            onChange={onChange}
                        />
                    </div>
                    {previewUrl && <img src={previewUrl} alt="Preview" style={{ width: '100px', height: '100px' }} />}
                    <div className={styles.formGroup}>
                        <label htmlFor="participants">인원 수</label>
                        <select
                            id="participants"
                            name="participants"
                            className={styles.select}
                            value={inputs.participants}
                            onChange={onChange}
                        >
                            <option value="" disabled>인원 수 선택</option>
                            <option value="1">1명</option>
                            <option value="2">2명</option>
                            <option value="3">3명</option>
                            <option value="4">4명</option>
                            <option value="5">5명</option>
                            <option value="6">6명</option>
                            <option value="7">7명</option>
                            <option value="8">8명</option>
                            <option value="9">9명</option>
                            <option value="10">10명 이상</option>
                        </select>
                    </div>
                    <div className={styles.formGroup}>
                        <label htmlFor="description">자세한 설명</label>
                        <textarea
                            id="description"
                            name="description"
                            placeholder="자세한 설명 입력"
                            value={inputs.description}
                            onChange={onChange}
                        />
                    </div>
                    <div className={styles.buttonGroup}>
                        <button type="submit" className={styles.submitButton}>작성완료 ➔</button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default CreatePostPage;
