import React, {useState} from 'react';
import {Link, useNavigate} from 'react-router-dom';
import styles from '../styles/loginpage.module.scss';
import plantImage from '../images/plant.png';
import exp from "node:constants";
import {EmptySignUp, SignUp} from "../interface/user";
import axios from "axios"; // 실제 식물 이미지 경로로 수정

const LoginPage = () => {
    const [data, setData] = useState<SignUp>(EmptySignUp)
    const baseUrl = "http://3.37.252.66:8080";
    const [inputs, setInputs] = useState({
        username: '',
        password: '',
    });

    const { username, password } = inputs;

    const onChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { value, id } = e.target;
        setInputs({
            ...inputs,
            [id]: value,
        });
    };

    const onAuthorityChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { value } = e.target;
        setInputs({
            ...inputs,
            authorityDtoSet: [{ authorityName: value }]
        });
    };

    const SignupOnClick = () => {
        axios.post(`${baseUrl}/articles/create`, {
            'title': '박사성',
            'content': '조민혁',
        })
            .then((response) => {
                if (response.status === 200) {
                    // const {username, name} = response.data
                    alert(response.data);
                    // setData(response.data);
                    console.log(data);
                } else {
                    alert('Signup failed. Please try again.');
                }
            })
            .catch(error => {
                if (error.response) {
                    alert(`Error: ${error.response.data}`);
                } else {
                    alert('Error during signup');
                }
            });

        setInputs({
            username: '',
            password: '',
            name: '',
            email: '',
            birth: '',
            introduction: '',
            gender: '',
            authorityDtoSet: [{ authorityName: '' }]
        });
    };

    // const navigate = useNavigate();
    // const onClick = () => {
    //     navigate('/signup')
    // }

    return (
        <div className={styles.loginContainer}>
            <div className={styles.leftSection}>
                <h1>로그인</h1>
                <img src={plantImage} alt="Plant" className={styles.plantImage} />
            </div>
            <div className={styles.rightSection}>
                <h2>Welcome to ECORUNNER</h2>
                <h3>로그인</h3>
                <div className={styles.loginForm}>
                    <label>사용자이름 혹은 이메일 주소</label>
                    <input type="text" placeholder="Username or Email Address" />
                    <label>비밀번호</label>
                    <input type="password" placeholder="Password" />
                    <button className={styles.loginButton}>로그인</button>
                    <div className={styles.signupLink} >
                        계정이 없으신가요? <Link to="/signup">회원가입</Link>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default LoginPage;
