import React, { useState } from 'react';
import styles from '../styles/signuppage.module.scss';

const SignupPage = () => {
    const [formData, setFormData] = useState({
        username: '',
        password: '',
        name: '',
        email: '',
        birth: '',
        introduction: '',
        gender: '',
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        console.log(formData);
    };

    return (
        <div className={styles.signupContainer}>
            <div className={styles.leftSection}>
                <h1>회원가입</h1>
                <img src="/images/plant.png" alt="Plant" className={styles.plantImage} />
            </div>
            <div className={styles.rightSection}>
                <h2>Welcome to ECORUNNER</h2>
                <h3>회원가입</h3>
                <form className={styles.signupForm} onSubmit={handleSubmit}>
                    <label>사용자이름</label>
                    <input type="text" name="username" value={formData.username} onChange={handleChange} placeholder="Username" required />

                    <label>비밀번호</label>
                    <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Password" required />

                    <label>이름</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Name" required />

                    <label>이메일</label>
                    <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" required />

                    <label>생년월일</label>
                    <input type="text" name="birth" value={formData.birth} onChange={handleChange} placeholder="Birth (YYMMDD)" required />

                    <label>자기소개</label>
                    <textarea name="introduction" value={formData.introduction} onChange={handleChange} placeholder="Introduction" required />

                    <label>성별</label>
                    <input type="text" name="gender" value={formData.gender} onChange={handleChange} placeholder="Gender" required />

                    <button type="submit" className={styles.signupButton}>회원가입</button>
                </form>
            </div>
        </div>
    );
};

export default SignupPage;
