import React from "react";
import {getPost} from "../interface/posts";
import styles from "../styles/detailpanel.module.scss"
import {useNavigate} from "react-router-dom";

interface pannelprops {
    post : getPost;
}

function DetailPannel( {post} : pannelprops) {
    const navigate = useNavigate();
    const onClick = () => {
        navigate(`/post/${post.id}`)
    }
    if (!post) return null;  // 포스트 정보가 없다면 아무것도 표시하지 않음

    return (
        <div className={styles.detailPanel}>
            <h2>{post.title}</h2>
            <div>
                <p><strong>Date:</strong></p> <p>{post.date}</p>
            </div>
            <div>
                <p><strong>Time:</strong></p> <p>{post.time}</p>
            </div>
            <div>
                <p><strong>Location:</strong></p> <p>{post.location}</p>
            </div>
            <div>
                <p><strong>Description:</strong></p> <p>{post.description}</p>
            </div>
            <button onClick={onClick}>게시글로 이동 ➔</button>
        </div>
    );
}

export default DetailPannel;
