import React, {useEffect, useState} from "react";
import styles from "../styles/mappage.module.scss";

declare global {
    interface Window {
        kakao: any;
    }
}

interface MapPageProps {
    selectedPlace: { lat: number, lng: number } | null;
}

function MapPage({ selectedPlace }: MapPageProps) {
    // 지도의 중심좌표
    const [center, setCenter] = useState<any>({
        lat: 33.450701,
        lng: 126.57066,
    });
    // 현재 위치
    const [position, setPosition] = useState<any>({
        lat: 0,
        lng: 0,
    });

    useEffect(() => {
        navigator.geolocation.getCurrentPosition((pos) => {
            setCenter({ lat: pos.coords.latitude, lng: pos.coords.longitude });
        });

        // navigator.geolocation.watchPosition((pos) => {
        //     setPosition({ lat: pos.coords.latitude, lng: pos.coords.longitude });
        // });

        const container = document.getElementById('map');
        const options = {
            center: new window.kakao.maps.LatLng(center.lng, center.lat),
            level: 3
        };
        const map = new window.kakao.maps.Map(container, options);

        if (selectedPlace) {
            const markerPosition = new window.kakao.maps.LatLng(selectedPlace.lat, selectedPlace.lng);
            const marker = new window.kakao.maps.Marker({
                position: markerPosition
            });

            // 마커를 클릭했을 때 마커 위에 표시할 인포윈도우를 생성합니다
            const iwContent = '<div style="padding:5px;">Hello World!</div>', // 인포윈도우에 표출될 내용으로 HTML 문자열이나 document element가 가능합니다
                iwRemoveable = true; // removeable 속성을 ture 로 설정하면 인포윈도우를 닫을 수 있는 x버튼이 표시됩니다

            // 인포윈도우를 생성합니다
            const infowindow = new window.kakao.maps.InfoWindow({
                content: iwContent,
                removable: iwRemoveable
            });

            marker.setMap(map);

            window.kakao.maps.event.addListener(marker, 'click', function () {
                infowindow.open(map, marker);
            });
        }
    }, [selectedPlace]);

    return (
        <div id="map" className={styles.map}>
        </div>
    );
}

export default MapPage;
