import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import styles from '../styles/createpostpage.module.scss';
import { Post, EmptyPost } from "../interface/posts"; // Assuming EmptyPost is exported

declare global {
    interface Window {
        kakao: any;
    }
}

interface CreatePostPageProps {
    addPost: (post: Post) => void;
}

function CreatePostPage({ addPost }: CreatePostPageProps) {
    const navigate = useNavigate();
    const baseUrl = "http://3.37.252.66:8080";
    const [inputs, setInputs] = useState<Post>(EmptyPost);

    const onChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
        const { name, value } = e.target;
        if (name === 'lat' || name === 'lng') {
            setInputs(prev => ({
                ...prev,
                place: {
                    ...prev.place,
                    [name]: parseFloat(value)
                }
            }));
        } else {
            setInputs(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    useEffect(() => {
        if (!inputs.location || !window.kakao || !window.kakao.maps) return;
        const ps = new window.kakao.maps.services.Places();
        ps.keywordSearch(inputs.location, (data: any[], status: any) => {
            if (status === window.kakao.maps.services.Status.OK && data.length) {
                setInputs(prev => ({
                    ...prev,
                    place: {
                        lat: data[0].y,
                        lng: data[0].x
                    }
                }));
            }
        });
    }, [inputs.location]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            const response = await axios.post(`${baseUrl}/index/articles/create`, inputs);
            if (response.status === 200) {
                alert('Post successful!');
                addPost(inputs);
                navigate('/community');
            } else {
                alert('Post failed. Please try again.');
            }
        } catch (error: any) {
            alert(`Error: ${error.response?.data || 'Error during post creation'}`);
        }
    };

    return (
        <div className={styles.pageContainer}>
            <form onSubmit={handleSubmit} className={styles.formContainer}>
                <label htmlFor="title">제목</label>
                <input type="text" name="title" value={inputs.title} onChange={onChange} />

                <label htmlFor="date">날짜</label>
                <input type="date" name="date" value={inputs.date} onChange={onChange} />

                <label htmlFor="time">시간</label>
                <input type="time" name="time" value={inputs.time} onChange={onChange} />

                <label htmlFor="location">장소</label>
                <input type="text" name="location" value={inputs.location} onChange={onChange} />

                <label htmlFor="participants">인원 수</label>
                <select name="participants" value={inputs.participants} onChange={onChange}>
                    <option value="">인원 수 선택</option>
                    {[...Array(10).keys()].map(n => <option key={n} value={n+1}>{n+1}명</option>)}
                </select>

                <label htmlFor="description">자세한 설명</label>
                <textarea name="description" value={inputs.description} onChange={onChange} />

                <button type="submit" className={styles.submitButton}>작성완료</button>
            </form>
        </div>
    );
}

export default CreatePostPage;
