import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from '../styles/createpostpage.module.scss';
import { Post } from '../interface/posts'

declare global {
    interface Window {
        kakao: any;
    }
}

interface CreatePostPageProps {
    addPost: (post: Post) => void;
}

const CreatePostPage = ({ addPost }: CreatePostPageProps) => {
    const navigate = useNavigate();
    const [inputs, setInputs] = useState<Post>({
        id: 0,
        title: '',
        location: '',
        participants: '',
        date: '',
        time: '',
        views: 0,
        description: '',
        content: '',
        lat: 0,
        lng: 0
    });

    const onChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
        const { name, value } = e.target;
        if (name === 'lat' || name === 'lng') {
            setInputs({
                ...inputs,
                [name]: parseFloat(value)
            });
        } else {
            setInputs({
                ...inputs,
                [name]: value
            });
        }
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const response = await axios.post("http://3.37.252.66:8080/index/articles/create", inputs);
        if (response.status === 200) {
            alert('Post successful!');
            addPost(inputs);
            navigate('/community');
        } else {
            alert('Post failed. Please try again.');
        }
    };

    return (
        <div className={styles.pageContainer}>
            <form onSubmit={handleSubmit} className={styles.formContainer}>
                <label htmlFor="title">Title</label>
                <input type="text" name="title" value={inputs.title} onChange={onChange} />

                <label htmlFor="date">Date</label>
                <input type="date" name="date" value={inputs.date} onChange={onChange} />

                <label htmlFor="time">Time</label>
                <input type="time" name="time" value={inputs.time} onChange={onChange} />

                <label htmlFor="location">Location</label>
                <input type="text" name="location" value={inputs.location} onChange={onChange} />

                <label htmlFor="participants">Participants</label>
                <input type="text" name="participants" value={inputs.participants} onChange={onChange} />

                <label htmlFor="description">Description</label>
                <textarea name="description" value={inputs.description} onChange={onChange} />

                <label htmlFor="lat">Latitude</label>
                <input type="number" name="lat" value={inputs.lat} onChange={onChange} />

                <label htmlFor="lng">Longitude</label>
                <input type="number" name="lng" value={inputs.lng} onChange={onChange} />

                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default CreatePostPage;
